W.W_SEL_GPi = -1 * rdn(4);
W.W_STN = 1 * rdn(5);
W.W_STN_GPe_out = 0.8 * rdn(6);
W.W_STN_GPe_inn = 0.8 * rdn(7);
W.W_STN_GPe_ta = 0.8 * rdn(8);
W.W_STN_GPi = 0.9 * rdn(9);
W.W_GPe_ta_CON = -0.25 * rdn(10);
W.W_GPe_ta_SEL = -0.25 * rdn(11);
W.W_GPe_ta_GPe_ta = -0.75 * rdn(12);
W.W_GPe_out_GPe_out = -0.75 * rdn(13);
W.W_GPe_inn_GPe_inn = -0.75 * rdn(14);
